mapper.select:
SELECT qnano, seqno, qnatype, title, content, memberno, mname, cnt, rdate, qpasswd, statement, indent, ansnum , r FROM ( SELECT qnano, seqno, qnatype, title, content, memberno, mname, cnt, rdate, qpasswd, statement, indent, ansnum, rownum as r FROM( SELECT qnano, seqno, qnatype, title, content, memberno, mname, cnt, rdate, qpasswd, statement, indent, ansnum FROM qna
WHERE qnatype LIKE '%' || #{word} || '%' ORDER BY seqno DESC, indent DESC, ansnum ASC
WHERE title LIKE '%' || #{word} || '%' ORDER BY seqno DESC, indent DESC, ansnum ASC
WHERE mname LIKE '%' || #{word} || '%' ORDER BY seqno DESC, indent DESC, ansnum ASC
ORDER BY seqno DESC, indent DESC, ansnum ASC
) ) WHERE r >= #{startNum} AND r <= #{endNum}

SELECT qnano, seqno, qnatype, title, content, memberno, mname, cnt, rdate, qpasswd, statement, indent, ansnum
FROM qna
WHERE qnano = #{qnano}

mapper.update:
UPDATE qna
SET cnt = cnt + 1
WHERE qnano = #{qnano}

UPDATE qna
SET qnatype=#{qnatype}, title=#{title}, content=#{content}, qpasswd=#{qpasswd}
WHERE qnano = #{qnano}

mapper.select:
SELECT mact
FROM member
WHERE memberno=#{memberno}

mapper.update:
UPDATE qna
SET statement = 1
WHERE qnano = #{qnano}
